Product.create!(name: "Air Force 1 Low 07", style: "315122-111/CW2288-111", color: "WHITE", price: 90.0, brand: "Nike", image: "https://images.stockx.com/360/Nike-Air-Force-1-Low-White-07/Images/Nike-Air-Force-1-Low-White-07/Lv2/img01.jpg?fm=avif&auto=compress&w=480&q=90&dpr=2&updated_at=1635275427", description: "The Nike Air Force 1 Low White 07 features an all-white leather upper with a perforated toe box and Swoosh overlays. A Nike heel embroidery and white sole completes the design.", release_date: Date.parse('2016-08-20'), seller_id: 2, category_id: 1)

Product.create!(name: "Yeezy Boost 350 V2", style: "HQ6316", color: "BONE", price: 230.0, brand: "Adidas", image: "https://images.stockx.com/360/adidas-Yeezy-Boost-350-V2-Pure-Oat/Images/adidas-Yeezy-Boost-350-V2-Pure-Oat/Lv2/img01.jpg?fm=avif&auto=compress&w=480&q=90&dpr=2&updated_at=1648479649", description: "The adidas Yeezy Boost 350 V2 Bone features a triple white Primeknit upper with mesh side stripes and canvas heel tabs. At the base, a semi-translucent sole with Boost technology completes the design.", release_date: Date.parse('2022-03-21'), seller_id: 2, category_id: 1)

Product.create!(name: "Jordan 4 Retro", style: "CT8527-700", color: "YELLOW", price: 220.0, brand: "Jordan", image: "https://images.stockx.com/360/Air-Jordan-4-Retro-Lightning-2021/Images/Air-Jordan-4-Retro-Lightning-2021/Lv2/img01.jpg?fm=avif&auto=compress&w=480&q=90&dpr=2&updated_at=1638476062", description: "Originally released in 2006, the Air Jordan 4 Retro Lightning (2021) is one of the most renowned retro Jordan 4 colorways in Jordan Brand's catalog. It features a Tour Yellow Durabuck upper with black netted panels and dark grey eyelets.", release_date: Date.parse('2021-03-21'), seller_id: 2, category_id: 1)

Product.create!(name: "Yeezy Boost 700", style: "GW0296", color: "WASH ORANGE", price: 240.0, brand: "Adidas", image: "https://images.stockx.com/360/adidas-Yeezy-Boost-700-Wash-Orange/Images/adidas-Yeezy-Boost-700-Wash-Orange/Lv2/img01.jpg?fm=avif&auto=compress&w=480&q=90&dpr=2&updated_at=1635732395", description: "The adidas Yeezy Boost 700 Wash Orange features a grey leather, suede, and mesh upper with teal panels and reflective underlays. At the base, a thick Wash Orange Boost midsole contrasts cool tones found in the upper.", release_date: Date.parse('2021-10-21'), seller_id: 2, category_id: 1)

